img_html_tmp = '<img alt="{city}" src="http://www.yeshiwei.com/fig/{dirname}">'
import os
import pandas as pd
rootDir = 'fig/'
dirNames = os.listdir(rootDir)
def makeCity(city):
    print(city)
    dirName = os.path.join(rootDir, city)
    files = os.listdir(dirName)
    files.sort(reverse=False)
    img_html = ""
    for f in files:
        if city in f:
            files.remove(f)
            files = [f, *files]
            break
    for f in files:
        img_html_ele = img_html_tmp.format(city=city, dirname=os.path.join(city, f)) + '\n'
        img_html += img_html_ele
    html = html_tmp.replace('{标题}', city + '分城区房价走势图')
    html = html.replace('{imghtml}', img_html)
    try:
        if city == 'allcity':
            df = pd.read_excel('rank/城市排名.xlsx')
        else:
            df = pd.read_excel('rank/%s区域排名.xlsx'%city)
        html = html.replace('{tablehtml}', df.to_html())
    except Exception as e:
        print(e)
        html = html.replace('{tablehtml}', "")
    fp = open('fangjia/%s.html'%city, 'w')
    fp.write(html)
    fp.close()
for city in dirNames:
    try:
        makeCity(city)
    except:
        pass
def exec(cmd):
    print(cmd)
    os.system(cmd)
exec('rsync -azP fig/ root@vps.yeshiwei.com:/var/www/html/fig')
exec('rsync -azP fangjia/ root@vps.yeshiwei.com:/var/www/html/fangjia')
